import React, { useState  } from 'react';
import useMousePosition from "../hooks/useMousePosition";
import hoverimg from "../utils/hoverimages"



export default function Hero() {

const {x , y}=useMousePosition();
const [isactive,setactive]=useState(false);

const activeTrue=()=>{
  setactive(true)
}
const activeFalse=()=>{
  setactive(false)
}

 return (
    <div className="hero"  >

        <div className="hero__intro">
        
            <p> <span onMouseEnter={activeTrue} onMouseLeave={activeFalse}>Ishika</span>  is a UX and Visual Designer who</p> 
      
            <p>creates experiences that are </p>
          
            <p>creative but human-centric</p>

        </div>


    </div>
  )
}
