import React, { useState } from 'react';
import reviewdata from "../utils/reviewdata";


export default function Reviews() {

const [stateindex,setstateindex]=useState(0);


  return (
    <div id='reviews'>
        <div className="reviews_header">
<p>Dont just take my word for it !</p>
        </div>
<div className="reviews_card_stack">
{reviewdata.map((data, index) => {
        return (
          <div
            className={index === stateindex ? "reviews_card active" : "reviews_card"}
            key={index}
          >
            {index === stateindex && (
              <div>
                <img src={data} alt="slide" className="image" />
              
              </div>
            )}
          </div>
        );
      })}
</div>
<button onClick={()=>console.log('set')}>click</button>
    </div>
    
  )
}
