import React, { useState  } from 'react';
import useMousePosition from "../hooks/useMousePosition";
import hoverimg from "../utils/hoverimages";



export default function Hero() {

const {x , y}=useMousePosition();
const [isactive,setactive]=useState(-1);

console.log(isactive);

 return (
    <div className="hero"  >

        <div className="hero__intro">
        
            <p> <span onMouseEnter={()=>{ setactive(0)}}  onMouseLeave={()=>{ setactive(-1)}}>Ishika</span>  is a UX and Visual Designer who</p> 
      
            <p>creates experiences that are </p>
          
            <p>creative but human-centric</p>

        </div>

        <div className="hero__media">

          {hoverimg.map((img,index)=>{ return <img key={index} className={isactive===index ? "active" : ""} src={img} alt="hoverimg" /> })}
        </div>


    </div>
  )
}
