import { useState, useRef, useEffect } from "react";

const useHeight=(data,refName)=>{

     
// using state for monitoring data

  const[dta,setdata]=useState(data);
  const[height,setheight]=useState();

// checking height of the elements so can fix height of the div
  const heightRef= useRef();

  function checkheight(){
    const Height = refName.current.clientHeight;
    setheight(Height)
  }
 

useEffect(() => {
  checkheight();

  
}, [dta])




 useEffect(() => {
 
 window.addEventListener("resize",checkheight);

  
 }, [])


 return height;
 
}

export default useHeight;
