import React, { useState,useEffect } from 'react';
import zifcareposter from "../media/zifcareposter.png";


const useMousePosition=()=>{
const [mousePosition,setMousePosition]=useState({x : 0 , y : 0});

useEffect(() => {

const updateMousePosition= event =>{
    setMousePosition({x : event.clientX,y:event.clientY})
}

  window.addEventListener('mousemove',updateMousePosition)

  return () => {
    window.removeEventListener('mousemove',updateMousePosition)
  }
}, [])


return mousePosition;
}

export default function ProjectItem() {

const [isactive,setactive]=useState(false);

    const {x,y}=useMousePosition();
    

  return (
    <div className={`project_item ${isactive ? "isactive" : ""}`} onMouseEnter={()=>{ setactive(true)  ;}} onMouseLeave={()=>{setactive(false) }} >
        <h1 style={isactive ? {opacity:1} : null}>Zifcare</h1>
        <p  style={isactive ? {opacity:1} : null}>- designed Zifcare web and Zifcare app</p>
        <img style={isactive? {transform:`translate(${x/10}%, ${y/10}%)`} : null} src={zifcareposter} alt="" />
    </div>
  )
}
