import React, { useState ,useRef,useEffect, useCallback } from 'react';
import useMousePosition from "../hooks/useMousePosition";
import hoverimg from "../utils/hoverimages";

const useSize=()=>{

  const [dimension,setdimension]=useState({});
  const [node,setnode]=useState(null);
  const ref=useCallback(node=>{
    setnode(node);
  },[])

  const getdimention=(node)=>{
    const rect =  node.getBoundingClientRect();

      return {
        width: rect.width,
        height: rect.height
      }

  }

  useLayoutEffect(() => {
   if(node){
      const measure = ()=>{ setdimension(getdimention(node))  }
   }
  }, [node])

  return [ref , dimension];
}


export default function Hero() {

const {x , y}=useMousePosition();
const [isactive,setactive]=useState(-1);


const [ref ,{width,height}]=useSize();s



 return (
    <div className="hero"  >

        <div className="hero__intro">
        
            <p> <span onMouseEnter={()=>{ setactive(0)}}  onMouseLeave={()=>{ setactive(-1)}}>Ishika</span>  is a <span onMouseEnter={()=>{ setactive(1)}}  onMouseLeave={()=>{ setactive(-1)}}>UX and Visual Designer</span>  who</p> 
      
            <p>creates experiences that are </p>
          
            <p><span  onMouseEnter={()=>{ setactive(2)}}  onMouseLeave={()=>{ setactive(-1)}}>creative</span>  but <span onMouseEnter={()=>{ setactive(3)}}  onMouseLeave={()=>{ setactive(-1)}}>human-centric</span> </p>

        </div>

        <div className="hero__media">

          {hoverimg.map((img,index)=>{ return <img  key={index} style={isactive===index? {transform:`translate(${x  }px , ${y}px)`} :  {transform:"translate(0 , 0)"}} className={isactive===index ? "active" : ""} src={img} alt="hoverimg" /> })}
        </div>


    </div>
  )
}
