import React, { useState ,useRef,useEffect } from 'react';
import hoverimg1 from "../media/hoverimg1.png"
import hoverimg2 from "../media/hoverimg2.png"
import hoverimg3 from "../media/hoverimg3.png"
import hoverimg4 from "../media/hoverimg4.png"
import useMousePosition from "../hooks/useMousePosition";




export default function Hero() {

const {x , y}=useMousePosition();
const [isactive,setactive]=useState(-1);

// using state for controling imgdata


const[imgheight,setheight]=useState();

// checking height of the images so can fix height of the div
const heightRef = useRef();

function checkheight(){
  const Height = heightRef.current.clientHeight;
  setheight(Height)
}


useEffect(() => {
checkheight();


}, [])




useEffect(() => {

window.addEventListener("resize",checkheight);


}, [])









 return (
    <div className="hero"  >

        <div className="hero__intro" >
        
            <p> <span onMouseEnter={()=>{ setactive(0)}}  onMouseLeave={()=>{ setactive(-1)}}>Ishika</span>  is a <span onMouseEnter={()=>{ setactive(1)}}  onMouseLeave={()=>{ setactive(-1)}}>UX and Visual Designer</span>  who</p> 
      
            <p>creates experiences that are </p>
          
            <p><span  onMouseEnter={()=>{ setactive(2)}}  onMouseLeave={()=>{ setactive(-1)}}>creative</span>  but <span onMouseEnter={()=>{ setactive(3)}}  onMouseLeave={()=>{ setactive(-1)}}>human-centric</span> </p>

        </div>

        <div className="hero__media">

        <img ref={heightRef }  style={isactive===0? {transform:`translate(${x}px , ${y}px)`,traansition:'transform 1s ease-in'} :  {transform:"translate(0 , 0)"}} className={isactive===0 ? "active" : ""} src={hoverimg1} alt="hoverimg" /> 
        <img ref={heightRef }  style={isactive===0? {transform:`translate(${x}px , ${y}px)`,traansition:'transform 1s ease-in'} :  {transform:"translate(0 , 0)"}} className={isactive===1 ? "active" : ""} src={hoverimg1} alt="hoverimg" /> 
        <img ref={heightRef }  style={isactive===0? {transform:`translate(${x}px , ${y}px)`,traansition:'transform 1s ease-in'} :  {transform:"translate(0 , 0)"}} className={isactive===2 ? "active" : ""} src={hoverimg1} alt="hoverimg" /> 
        <img ref={heightRef }  style={isactive===0? {transform:`translate(${x}px , ${y}px)`,traansition:'transform 1s ease-in'} :  {transform:"translate(0 , 0)"}} className={isactive===3 ? "active" : ""} src={hoverimg1} alt="hoverimg" /> 
        </div>


    </div>
  )
}
